import React from "react";
import { Div, Icon } from "atomize";

const Loading = () => {
  return (
    <Div
      bg="transparent"
      d="flex"
      align="center"
      justify="center"
      pos="absolute"
      top="0"
      bottom="0"
      right="0"
      left="0"
      style={{ zIndex: -1 }}
    >
      <Icon name="Loading3" size="4rem" color="brand500" />
    </Div>
  );
};

