    },[fetchAllProducts])

    if (!products) return <div>loading</div>


    return(
        <Container>
            <Row>
                {products.map(product => (
                    <Col key = {product.id}>
                        <Link to={`/product`}>

                        </Link>
                    </Col>
                ))}
            </Row>
        </Container>
    )
}

export default HomePage
